def calcMunsellColors(T_MATRIX, waves):
    mspds = []
    for munsellTarget in munsellMaxColorfullness:
        mspd = XYZ_to_spectral_1(np.array(munsellTarget), T_MATRIX, waves=waves)
        mspds.append(mspd)
    return mspds
